select * from (select [T3_費用年月], D2_流水編號, [D3_身份證統一編號], [D30_診治醫事人員代號], 姓名 from (select [T3_費用年月], D2_流水編號, [D3_身份證統一編號], [D30_診治醫事人員代號] FROM [192.168.255.12].[NTPC_Report].[dbo].[dtlfa_o_XML] where [T3_費用年月] BETWEEN '%s' AND '%s' and ([D19_國際疾病分類碼一] in (select ICD_10_代碼 from 感染科.dbo.URI診斷碼) or [D20_國際疾病分類碼二] in (select ICD_10_代碼 from 感染科.dbo.URI診斷碼) or [D21_國際疾病分類碼三] in (select ICD_10_代碼 from 感染科.dbo.URI診斷碼) or [D22_國際疾病分類碼四] in (select ICD_10_代碼 from 感染科.dbo.URI診斷碼) or [D23_國際疾病分類碼五] in (select ICD_10_代碼 from 感染科.dbo.URI診斷碼))) as A	left join [192.168.255.12].[HIS基本資料].[dbo].[醫師資料表] as 醫師資料 on A.[D30_診治醫事人員代號] = 醫師資料.[身分證統一編號]) as AA inner join (SELECT T3_費用年月, D2_流水編號, D3_身份證統一編號, P4_藥品項目代號 FROM [192.168.255.12].[NTPC_Report].[dbo].[ordfa_o_XML] where [T3_費用年月] = '%s' and P4_藥品項目代號 IN (SELECT [健保碼] FROM [感染科].[dbo].[抗生素代碼])) as BB ON AA.[T3_費用年月] = BB.[T3_費用年月] and AA.[D2_流水編號] = BB.[D2_流水編號] and AA.[D3_身份證統一編號] = BB.[D3_身份證統一編號] 