INSERT INTO [AFDBS].[dbo].[醫療部門.健檢中心.兵役體檢上傳資料] ([REGNO], [IDNO], [REQ_DATE], [NAME], [NAME_C], [RESULT], [PKEY_CASENO]) SELECT * FROM 	OPENQUERY([ORACLE], '		Select B.REGNO, I.IDNO, B.REQ_DATE, I.NAME, C.NAME_C, A.RESULT, PKEY_CASENO 		From FEX002K A, FEX001K B, FEX030K C, FEX032K D, FEX033K E, FEX035K G, FEX037K H, FTH008K I, FTH007K J Where (A.STATUS <> ''3'' or A.STATUS is null) And (A.CODE_TYPE = ''3'' OR A.CODE_TYPE = ''2'') And A.CONFIRM_FLAG = ''Y'' And A.RESULT Is Not NULL And A.SAMPLE_ID = B.SAMPLE_ID And A.TEST = C.TEST(+) And A.CLASS = D.TEST_TYPE(+) And A.SAMPLE = E.SAMPLE_CODE(+) And A.LOCATE = G.LOCATE(+) And A.MACH_NO = H.MACH_NO(+) And B.REGNO = I.REGNO And B.DEPT = J.DEPT(+) And SUBSTR(B.REGNO,LENGTH(B.REGNO)-1,2) >= ''00'' And SUBSTR(B.REGNO,LENGTH(B.REGNO)-1,2) <= ''99'' And B.SOURCE = ''5'' and (J.LABCODE <> ''2B'' OR J.LABCODE IS NULL) And G.KIND(+) = ''1'' And B.HEATH_CARD = ''11'' And B.REQ_DATE >= ''%s'' And B.REQ_DATE <= ''%s'' Order by B.REGNO asc ')